import React, { useState } from "react";
import "./App.css";
import { Route, Routes } from "react-router-dom";

import Navbar from "./components/Navbar";
import Home from "./components/Home";
import Store from "./components/Store";
import Cart from "./components/Cart";

function App() {
  const [stock, setStock] = useState([
    {
      name: "David Beckham",
      desc: "One of the best midfielders ever",
      price: 37000000,
      quantity: 1,
      img: "./images/beckham.jpeg",
      id: 1,
    },
    {
      name: "Ronaldo",
      desc: "R9. Brazil's greatest forward.",
      price: 100000000,
      quantity: 1,
      img: "./images/r9.jpeg",
      id: 2,
    },
    {
      name: "Messi",
      desc: "The greatest of all time.",
      price: 150000000,
      quantity: 1,
      img: "./images/messi.jpeg",
      id: 3,
    },
  ]);

  return (
    <div className="App">
      <Navbar />

      <Routes>
        <Route index element={<Home />} />
        <Route path="/store" element={<Store stock={stock} />} />
        <Route path="/cart" element={<Cart />} />
      </Routes>
    </div>
  );
}

export default App;
