import React, { useState } from "react";
import "./styles/Store.css";
import StoreItem from "./StoreItem";
import Navbar from "./Navbar";

const Store = () => {
  const [stock, setStock] = useState([
    {
      name: "David Beckham",
      desc: "One of the best midfielders ever",
      price: 37000000,
      quantity: 1,
      img: "./images/beckham.jpeg",
      id: 1,
    },
    {
      name: "Ronaldo",
      desc: "R9. Brazil's greatest forward.",
      price: 100000000,
      quantity: 1,
      img: "./images/r9.jpeg",
      id: 2,
    },
    {
      name: "Messi",
      desc: "The greatest of all time.",
      price: 150000000,
      quantity: 1,
      img: "./images/messi.jpeg",
      id: 3,
    },
  ]);

  const mappedStock = stock.map((item) => {
    return (
      <StoreItem
        name={item.name}
        desc={item.desc}
        price={item.price}
        quantity={item.quantity}
        img={item.img}
        key={item.id}
      />
    );
  });

  return (
    <div className="">
      <Navbar />
      <h1 className="store--title">Store</h1>
      <div className="store">{mappedStock}</div>
    </div>
  );
};

export default Store;
