import React, { useState } from "react";
import "./App.css";
import { Route, Routes } from "react-router-dom";
import stockArray from "./stock";

import Navbar from "./components/Navbar";
import Home from "./components/Home";
import Store from "./components/Store";
import About from "./components/About";
import Cart from "./components/Cart";

function App() {
  const [stock, setStock] = useState(stockArray);
  const [cart, setCart] = useState([]);
  const [isCartshown, setIsCartShown] = useState(true);

  const showCart = () => {
    setIsCartShown((prevState) => !prevState);
  };

  const addToCart = (id) => {
    const index = stock.findIndex((item) => item.id === id);
    console.log(
      cart.filter((item) => item.id === stock[index].id).length === 0
    );
    // if (cart.filter((item) => item.id === stock[index].id)) {
    //   const indexOfCartItem = cart.findIndex((item) => item.id === id);
    //   const updatedItem = cart[indexOfCartItem].quantity + 1;
    //   setCart((prevCart) => [...prevCart, updatedItem]);
    // }
    const itemToBeAdded = {
      name: stock[index].name,
      price: stock[index].price,
      quantity: 1,
      img: stock[index].img,
      id: stock[index].id,
    };
    setCart((prevCart) => [...prevCart, itemToBeAdded]);
  };

  return (
    <div className="App">
      <Navbar showCart={showCart} cartItemAmount={cart.length} />
      {isCartshown && <Cart cart={cart} />}

      <Routes>
        <Route index element={<Home />} />
        <Route
          path="/store"
          element={<Store stock={stock} addToCart={addToCart} />}
        />
        <Route path="/about" element={<About />} />
      </Routes>
    </div>
  );
}

export default App;
