import React, { useState } from "react";
import "./App.css";
import { Route, Routes } from "react-router-dom";
import stockArray from "./stock";

import Navbar from "./components/Navbar";
import Home from "./components/Home";
import Store from "./components/Store";
import About from "./components/About";
import Cart from "./components/Cart";

function App() {
  const [stock, setStock] = useState(stockArray);
  const [cart, setCart] = useState([
    { name: "Test Item", price: 2000000, quantity: 1 },
  ]);
  const [isCartshown, setIsCartshown] = useState(true);

  // const addToCart = () => {
  //   setCart
  // }

  return (
    <div className="App">
      <Navbar />
      {isCartshown && <Cart cart={cart} />}

      <Routes>
        <Route index element={<Home />} />
        <Route path="/store" element={<Store stock={stock} />} />
        <Route path="/about" element={<About />} />
      </Routes>
    </div>
  );
}

export default App;
